installation.script:
#!/usr/bin/env bash
set -euo pipefail
IFS=$'\n\t'

#----------------------------------------
# Pterodactyl DayZ (Standalone) Egg Installer
# Author: BeamHost
# Date: 2025/07/20
#----------------------------------------

# --- Color Codes ---
RESET='\033[0m'
CYAN='\033[1;36m'
YELLOW='\033[1;33m'
RED='\033[1;31m'
GREEN='\033[1;32m'
BOLD='\033[1m'

# --- Emoji Shortcuts ---
INFO_EMOJI="ℹ️ "
WARN_EMOJI="⚠️ "
ERROR_EMOJI="❌"
OK_EMOJI="✅"
TOOL_EMOJI="🛠️"
TIME_EMOJI="🕒"

# --- Functions for Clean Output ---
info()  { echo -e "${CYAN}${INFO_EMOJI}[$(date +'%H:%M:%S')] [INFO]${RESET} $1"; }
warn()  { echo -e "${YELLOW}${WARN_EMOJI}[$(date +'%H:%M:%S')] [WARN]${RESET} $1"; }
error() { echo -e "${RED}${ERROR_EMOJI}[$(date +'%H:%M:%S')] [ERROR]${RESET} $1"; }
ok()    { echo -e "${GREEN}${OK_EMOJI}[$(date +'%H:%M:%S')] [OK]${RESET} $1"; }

# --- Parameter Validation ---
: "${STEAM_USER:?STEAM_USER must be set}"
: "${STEAM_PASS:?STEAM_PASS must be set}"
: "${STEAMCMD_APPID:?STEAMCMD_APPID must be set}"
STEAMCMD_EXTRA_FLAGS=${STEAMCMD_EXTRA_FLAGS:-}

DEFAULT_HOSTNAME=${DEFAULT_HOSTNAME:-"EXAMPLE NAME"}
DEFAULT_DESCRIPTION=${DEFAULT_DESCRIPTION:-"Some description"}
DEFAULT_PASSWORD=${DEFAULT_PASSWORD:-""}
DEFAULT_PASSWORDADMIN=${DEFAULT_PASSWORDADMIN:-""}
QUERY_PORT=${QUERY_PORT:-2303}

SERVER_DIR=/mnt/server
STEAMCMD_DIR=$SERVER_DIR/steamcmd
STEAMAPPS_DIR=$SERVER_DIR/steamapps

info "${TOOL_EMOJI}Starting DayZ install (BeamHost) at $(date -u +'%F %T UTC')"

# --- Prepare Directories ---
info "Preparing server directories..."
mkdir -p "$STEAMCMD_DIR" "$STEAMAPPS_DIR"

# --- SteamCMD Install ---
if [[ ! -x "$STEAMCMD_DIR/steamcmd.sh" ]]; then
  info "Downloading SteamCMD..."
  cd /tmp
  curl -sSL -o steamcmd.tar.gz https://steamcdn-a.akamaihd.net/client/installer/steamcmd_linux.tar.gz
  tar -xzvf steamcmd.tar.gz -C "$STEAMCMD_DIR"
  rm -f steamcmd.tar.gz
  ok "SteamCMD installed to $STEAMCMD_DIR"
else
  info "SteamCMD already present, skipping download."
fi

# --- Fix Permissions & Env ---
info "Setting ownership and environment variables..."
chown -R root:root "$SERVER_DIR"
export HOME="$SERVER_DIR"

# --- Install/Update DayZ server ---
info "Updating/installing DayZ server with SteamCMD... ${TIME_EMOJI}"
"$STEAMCMD_DIR/steamcmd.sh" \
  +force_install_dir "$SERVER_DIR" \
  +login "$STEAM_USER" "$STEAM_PASS" \
  +app_update "$STEAMCMD_APPID" $STEAMCMD_EXTRA_FLAGS validate \
  +quit
ok "DayZ server install/update complete!"

# --- 32/64-bit Steam Libraries ---
info "Setting up Steam libraries..."
mkdir -p "$SERVER_DIR/.steam/sdk32" "$SERVER_DIR/.steam/sdk64"

if [[ -f "$STEAMCMD_DIR/linux32/steamclient.so" ]]; then
  cp -v "$STEAMCMD_DIR/linux32/steamclient.so" "$SERVER_DIR/.steam/sdk32/steamclient.so"
  ok "linux32/steamclient.so copied!"
else
  warn "$STEAMCMD_DIR/linux32/steamclient.so not found!"
fi

if [[ -f "$STEAMCMD_DIR/linux64/steamclient.so" ]]; then
  cp -v "$STEAMCMD_DIR/linux64/steamclient.so" "$SERVER_DIR/.steam/sdk64/steamclient.so"
  ok "linux64/steamclient.so copied!"
else
  warn "$STEAMCMD_DIR/linux64/steamclient.so not found!"
fi

# --- Write Default Config ---
info "Writing default serverDZ.cfg..."
cat <<EOF > "$SERVER_DIR/serverDZ.cfg"
hostname = "$DEFAULT_HOSTNAME";
description = "$DEFAULT_DESCRIPTION";
password = "$DEFAULT_PASSWORD";
passwordAdmin = "$DEFAULT_PASSWORDADMIN";
steamQueryPort=$QUERY_PORT;

enableWhitelist = 0;
disableBanlist = false;
disablePrioritylist = false;

maxPlayers = 60;
verifySignatures = 2;
forceSameBuild = 1;

disableVoN = 0;
vonCodecQuality = 20;

disable3rdPerson = 0;
disableCrosshair = 0;

serverTime = "SystemTime";
serverTimeAcceleration = 1;
serverNightTimeAcceleration = 1;
serverTimePersistent = 0;

guaranteedUpdates = 1;
loginQueueConcurrentPlayers = 5;
loginQueueMaxPlayers = 500;
instanceId = 1;
storageAutoFix = 1;

class Missions {
  class DayZ {
    template = "dayzOffline.chernarusplus";
  };
};

respawnTime = 5;
motd[] = { "line1","line2" };
motdInterval = 1;

timeStampFormat = "Short";
logAverageFps = 1;
logMemory = 1;
logPlayers = 1;
logFile = "server_console.log";

adminLogPlayerHitsOnly = 0;
adminLogPlacement = 0;
adminLogBuildActions = 0;
adminLogPlayerList = 0;

disableMultiAccountMitigation = false;
enableDebugMonitor = 1;
allowFilePatching = 1;

simulatedPlayersBatch = 20;
multithreadedReplication = 1;
speedhackDetection = 1;

networkRangeClose = 20;
networkRangeNear = 150;
networkRangeFar = 1000;
networkRangeDistantEffect = 4000;
networkObjectBatchLogSlow = 5;
networkObjectBatchEnforceBandwidthLimits = 1;
networkObjectBatchUseEstimatedBandwidth = 0;
networkObjectBatchUseDynamicMaximumBandwidth = 1;
networkObjectBatchBandwidthLimit = 0.8;
networkObjectBatchCompute = 1000;
networkObjectBatchSendCreate = 10;
networkObjectBatchSendDelete = 10;

defaultVisibility = 1375;
defaultObjectViewDistance = 1375;

lightingConfig = 0;
disablePersonalLight = 1;

disableBaseDamage = 0;
disableContainerDamage = 0;
disableRespawnDialog = 0;

pingWarning = 200;
pingCritical = 250;
MaxPing = 300;
serverFpsWarning = 15;

shotValidation = 1;
EOF
ok "serverDZ.cfg created!"

ok "DayZ installation complete at $(date -u +'%F %T UTC')"
